import React from "react";
import { Container, Row } from "reactstrap"; // Removed Button since it's not used
import { NavLink } from "react-router-dom"; // Removed Link since it's not used

// earlier it was like this 
// import React from "react";
// import {Container, Row, Button} from 'reactstrap';
// import { NavLink, Link } from "react-router-dom";


import logo from "../../assets/images/logo.png";

const nav_links = [
  { path: "/home", display: "Home" },
  { path: "#", display: "About" },
  { path: "/tours", display: "Tours" }, // Fixed path from './tours' to '/tours'
];

const Header = () => {
  return (
    <header className="header">
      <Container>
        <Row>
          <div className="nav_wrapper d-flex align-items-center justify-content-between">
            {/*------ Logo ------*/}
            <div className="logo">
              <img src={logo} alt="Logo" /> {/*âœ… Fixed: Removed quotes around {logo} */}
            </div>
            {/*------ Logo end ------*/}

            {/*------ menu starts------*/}
            <div className="navigation">
              <ul className="menu d-flex align-items-center gap-5">
                {nav_links.map((item, index) => (
                    <li className="nav_item" key={index}>
                      <Navlink to={item.path}>{item.display}</Navlink>
                    </li>
                  ))}
              </ul>
            </div>
            {/*------ menu end ------*/}

            <div className="nav_right d-flex align-items-center gap-4">
              <div className="nav_btns d-flex align-items-center gap-4">
                <button></button>
              </div>
            </div>
          </div>
        </Row>
      </Container>
    </header>
  );
};

export default Header;
